import React, { useEffect, useState } from "react";
import Footer from "../components/footer/Footer";
import Sidebar from "../components/Sidebar/Sidebar";
import imageBlog from "../assets/images/blogger-to-WordPress.jpeg";
import axios from "axios";
import { useParams } from "react-router-dom";

const PostId = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [postId, setPostId] = useState({});
  const { id } = useParams();
  const getPostId = async () => {
    setIsLoading(true);
    try {
      const { data } = await axios.get(
        `https://jsonplaceholder.typicode.com/posts/${id ? `${id}` : ""}`
      );
      setPostId(data);
      console.log(data);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    getPostId();
  }, []);

  return (
    <div>
      <div
        className="App"
        //   style={{
        //     background: "#fff",
        //   }}
      >
        {isLoading ? (
          renderLoading()
        ) : (
          <div className="box">
            <div
              className="content main"
              style={{
                background: "#e6e9ee",
                //   height: "auto",
                //   paddingTop: "3px",
              }}
            >
              <div>
                <div>
                  <div className="blogHeader">
                    <h1
                      style={{
                        textAlign: "center",
                        // paddingTop: "0px",
                        paddingBottom: "20px",
                      }}
                    >
                      {postId.title}
                    </h1>
                    <p className="entry-meta">
                      <time className="entry-time">March 18, 2023</time>{" "}
                      <span className="entry-author">
                        <a href="#" className="entry-link">
                          <span className="entry-author-name">
                            Colin Newcomer
                          </span>
                        </a>
                      </span>
                      <span>
                        <a href="#" className="entry-link">
                          Comments
                        </a>
                      </span>
                    </p>
                  </div>

                  <img src={imageBlog} alt="blog" />
                  <p className="postDec">{postId.body}â€¦.</p>
                </div>
              </div>
            </div>
            <Sidebar />
          </div>
        )}
      </div>

      <Footer />
    </div>
  );
};

export default PostId;
