import axios from "axios";
import React, { useEffect, useState } from "react";
import { Spinner } from "react-bootstrap";
import imageBlog from "./assets/images/blogger-to-WordPress.jpeg";
import Footer from "./components/footer/Footer";
import Menu from "./components/navbar/menu/Menu";
import Navbar from "./components/navbar/Navbar";
import Sidebar from "./components/Sidebar/Sidebar";

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const getData = async () => {
    setIsLoading(true);
    try {
      const { data } = await axios.get(
        "https://jsonplaceholder.typicode.com/posts"
      );
      setPosts([...data]);
      console.log(data);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
    }
  };

  const renderLoading = () => {
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <Spinner animation="grow" variant="dark" />
    </div>;
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Navbar isOpen={isOpen} setIsOpen={setIsOpen} />
      {isOpen ? <Menu /> : null}
      <div className="App">
        {isLoading ? (
          renderLoading()
        ) : (
          <div className="box">
            <div className="content main">
              <div>
                {posts &&
                  posts.slice(0, 5).map((post) => (
                    <div key={post.id}>
                      <div className="blogHeader">
                        <span className="buttonBlog">
                          <a href="#">WordPress Tutorials</a>{" "}
                        </span>
                        <h1>{post.title}</h1>
                        <p className="entry-meta">
                          <time className="entry-time">March 18, 2023</time>{" "}
                          <span className="entry-author">
                            <a href="#" className="entry-link">
                              <span className="entry-author-name">
                                Colin Newcomer
                              </span>
                            </a>
                          </span>
                          <span>
                            <a href="#" className="entry-link">
                              Comments
                            </a>
                          </span>
                        </p>
                      </div>

                      <img src={imageBlog} alt="blog" />
                      <p className="postDec">
                        {post.body}â€¦.
                        <a className="blogDetails" href="#">
                          Continue Reading
                        </a>
                      </p>
                      <hr />
                    </div>
                  ))}
              </div>
            </div>
            <Sidebar />
          </div>
        )}
      </div>
      {!isLoading && <Footer />}
    </>
  );
}

export default App;
