import axios from "axios";
import React, { useEffect, useState } from "react";
import imageBlog from "./assets/images/blogger-to-WordPress.jpeg";
import Menu from "./components/navbar/menu/Menu";
import Navbar from "./components/navbar/Navbar";

function App() {
  const [isOpen, setIsOpen] = useState(false);
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const getData = async () => {
    setIsLoading(true);
    try {
      const { data } = await axios.get(
        "https://jsonplaceholder.typicode.com/posts"
      );
      setPosts([...data]);
      console.log(data);
      setIsLoading(false);
    } catch (error) {
      setIsLoading(false);
    }
  };

  const renderLoading = () => {
    <div className="loading">
      <span class="loader"></span>
    </div>;
  };

  useEffect(() => {
    getData();
  }, []);

  return (
    <>
      <Navbar isOpen={isOpen} setIsOpen={setIsOpen} />
      {isOpen ? <Menu /> : null}
      <div className="App">
        {isLoading ? (
          renderLoading()
        ) : (
          <div className="box">
            <div className="content main">
              <div>
                {posts &&
                  posts.slice(0, 5).map((post) => (
                    <div key={post.id}>
                      <div className="blogHeader">
                        <span className="buttonBlog">
                          <a href="#">WordPress Tutorials</a>{" "}
                        </span>
                        <h1>{post.title}</h1>
                        <p className="entry-meta">
                          <time className="entry-time">March 18, 2023</time>{" "}
                          <span className="entry-author">
                            <a href="#" className="entry-link">
                              <span className="entry-author-name">
                                Colin Newcomer
                              </span>
                            </a>
                          </span>
                          <span>
                            <a href="#" className="entry-link">
                              Comments
                            </a>
                          </span>
                        </p>
                      </div>

                      <img src={imageBlog} alt="blog" />
                      <p className="postDec">
                        {post.body}â€¦.
                        <a className="blogDetails" href="#">
                          Continue Reading
                        </a>
                      </p>
                      <hr />
                    </div>
                  ))}
              </div>
            </div>

            <div className="sidebar">
              <section className="content form">
                <h4 className="form-title">OUR NEWSLETTER</h4>
                <p>Get awesome content delivered straight to your inbox.</p>
                <div>
                  <form>
                    <input
                      type="text"
                      placeholder="Enter your email address "
                      className="emailAddress"
                    />
                    <button type="submit">GET APDATES</button>
                  </form>
                </div>
              </section>
              <section className="content post-title">
                <div className="title-post">
                  <h4 className="header-title">THE BEST OF WPKUBE</h4>
                  <p className="personal-title">
                    Some of the best content we have published so far.
                  </p>
                  <h4 className="text-title">BEGINNER GUIDES & REVIEWS</h4>
                </div>
                <div className="menu-post">
                  <p>
                    <a href="#">
                      <span className="item-count">1</span>8 Best Cheap
                      WordPress Hosting Providers in 2023 (From $1.99)
                    </a>
                  </p>
                  <hr />
                  <p>
                    <a href="#">
                      <span className="item-count">2</span>10 Best WordPress
                      Hosting Options for 2023 (Pros & Cons)
                    </a>
                  </p>
                  <hr />
                  <p>
                    <a href="#">
                      <span className="item-count">3</span>8 Best Managed
                      WordPress Hosting Providers for 2023 Compared
                    </a>
                  </p>
                  <p>
                    <a href="#">
                      <span className="item-count">4</span>5 Best WooCommerce
                      Hosting Providers Compared in 2023 (All Budgets)
                    </a>
                  </p>
                  <p>
                    <a href="#">
                      <span className="item-count">5</span>Top 9 Landing Page
                      Plugins for WordPress (2023)
                    </a>
                  </p>
                </div>
              </section>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default App;
